import styled, { css } from "styled-components";

const hoverStyle = css`
  * {
    transition: color 0.3s;
    &:hover {
      color: ${(props) => props.theme.blue};
    }
  }
`;

const inputStyle = css`
  width: 440px;
  height: 60px;
  padding: 0 16px;
  font-size: 16px;
  color: ${({ theme }) => theme.white.lighter};
  border: none;
  border-radius: 8px;
`;

//common
export const Wrapper = styled.main`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: ${({ theme }) => theme.black.darker};
  overflow-y: hidden;
`;
export const Form = styled.form`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

export const Logo = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  height: 100px;
  padding-left: 60px;
`;

export const Title = styled.h1`
  color: ${({ theme }) => theme.white.lighter};
  font-size: 40px;
  margin-bottom: 20px;
`;
export const InputWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

export const InputContainer = styled.div`
  position: relative;
  transition: all 500ms;
`;
export const Input = styled.input<{ hasError?: boolean }>`
  ${inputStyle}
  margin-top: 20px;
  padding-top: 10px;
  padding: 30px 0 18px 20px;
  border: 1px solid ${(props) => (props.hasError ? "inherit" : "inherit")};
  background: ${({ theme }) => theme.black.lighter};
  &:focus {
    outline: none;
    border-color: ${(props) => (props.hasError ? "#f00" : "inherit")};
  }
`;
export const Label = styled.label<{ hasError?: boolean }>`
  position: absolute;
  top: 50%;
  left: 16px;
  color: ${(props) => (props.hasError ? "#f00" : props.theme.white.lighter)};
  user-select: none;
  transition: transform 150ms cubic-bezier(0.4, 0, 0.2, 1),
    color 150ms cubic-bezier(0.4, 0, 0.2, 1), top 150ms;
  &.focused {
    top: 0;
    left: 20px;
    transform: scale(0.9) translateY(150%);
    transform-origin: left;
    color: ${(props) => (props.hasError ? "#f00" : props.theme.blue)};
  }
`;

export const Submit = styled.input`
  ${inputStyle}
  margin-top: 40px;
  font-size: 16px;
  font-weight: bold;
  background: ${({ theme }) => theme.blue};
  transition: all 0.3s;
  cursor: pointer;
  &:hover {
    opacity: 0.8;
  }
`;

//login
export const Desc = styled.p`
  font-size: 14px;
  color: ${({ theme }) => theme.gray.lighter};
  padding: 10px 0;
`;
export const FindWapper = styled.div`
  width: 250px;
  margin: 50px 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 30px;
  color: ${(props) => props.theme.white.lighter};

  .findauth {
    display: flex;
    justify-content: space-between;
    padding: 0 30px;
    ${hoverStyle}
    cursor: pointer;
  }

  .goToSignup {
    display: flex;
    align-items: center;
    justify-content: space-between;
    transition: all 0.3s;
    p {
      color: ${({ theme }) => theme.gray.lighter};
    }
    .signup {
      cursor: pointer;
      transition: all 0.3s;

      &:hover {
        color: ${(props) => props.theme.blue};
      }
    }
  }
`;

//signup
export const Text = styled.p`
  font-size: 18px;
  color: ${({ theme }) => theme.gray.lighter};
`;

export const VerificationWrapper = styled.div`
  display: flex;
  gap: 10px;
  width: 440px;
  align-items: center;
  #phoneNum {
    padding-top: 10px;
    padding-left: 20px;
  }
  input {
    border: 1px solid #f00;
    width: 20%;
    ${inputStyle}
    transition: all 0.3s;
    cursor: pointer;
    &:hover {
      opacity: 0.8;
    }
  }
`;
