import styled from "styled-components";

export const Form = styled.form`
  margin-top: 60px;
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: ${({ theme }) => theme.black.darker};
`;
export const Title = styled.h1`
  color: ${({ theme }) => theme.white.lighter};
  font-size: 40px;
  margin-bottom: 20px;
`;
export const InputWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
export const Input = styled.input`
  width: 440px;
  height: 60px;
  margin-top: 20px;
  padding: 0 16px;
  font-size: 16px;
  color: ${({ theme }) => theme.white.lighter};
  border: none;
  border-radius: 8px;
  background: ${({ theme }) => theme.black.lighter};
  &::placeholder {
    color: ${({ theme }) => theme.white.lighter};
  }
  &:focus {
    outline: none;
  }
  &:nth-child(3) {
    margin-top: 40px;
    background: ${({ theme }) => theme.blue};
    transition: all 0.3s;
    cursor: pointer;
    &:hover {
      opacity: 0.8;
    }
  }
`;
export const FindWapper = styled.div`
  width: 400px;
  margin: 40px 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding-bottom: 10px;
  color: ${({ theme }) => theme.white.lighter};
  cursor: pointer;
  .findauth {
    /* width: 100%; */
    display: flex;
    gap: 120px;
  }
  &:hover {
    text-decoration: underline;
  }
  .goToSignup {
    /* width: 100%; */
    display: flex;
    gap: 80px;
  }
`;
